		alert("<?php echo $msgstr["limituserdate"]?>"+": "+p[15])				return			}		}		if (Trim(p[16])!=""){			if (fecha_d>p[16]){				alert("<?php echo $msgstr["limitobjectdate"]?>"+": "+p[16])				return			}		}		if (nMultas!=0){			alert("<?php echo $msgstr["norenew"]?>")			return		}		document.renovar.submit()	}else{		alert("<?php echo $msgstr["markloan"]?>")	}}</script></head><body><?phpif (isset($def["LOGO"]))echo "<img src=".$def["LOGO"].">";?><br><table width=100%><td><h4>Loans Status</h4><p><form name=ecta><?phpinclude("leer_pft.php");// se lee la configuración localinclude("calendario_read.php");include("locales_read.php");// SE LEE LA RUTINA PARA CALCULAR EL LÍMITE DE LA SUSPENSIONinclude("fecha_de_devolucion.php");// se leen las politicas de préstamoinclude("loanobjects_read.php");// se lee la configuración de la base de datos de usuariosinclude("borrowers_configure_read.php");$ec_output="";include("ec_include.php");echo $ec_output;if (count($prestamos)>0) {	echo  "<strong><a href=javascript:Renovar()>".$msgstr["renew"]."</a></strong><p>";	if (isset($arrHttp["vienede"]) and $arrHttp["vienede"]=="ecta_web") echo $msgstr["opac_ecta"];}//SE LEEN LAS RESERVAS PENDIENTESinclude("../reserve/reserves_read.php");$reservas_activas=0;$cuenta=0;$reserves_arr=ReservesRead("CU_".$arrHttp["usuario"],"S","","N");$reserves_user=$reserves_arr[0];echo $reserves_user;?></form><form name=renovar action=renovar_ex.php method=post><input type=hidden name=searchExpr><input type=hidden name=usuario value=<?php echo $arrHttp["usuario"]?>><input type=hidden name=vienede value=ecta_web><input type=hidden name=DB_PATH value=<?php echo $arrHttp["DB_PATH"]?>><input type=hidden name=lang value=<?php echo $arrHttp["lang"]?>></form><form name=anular method=post action=../circulation/reservar_anular.php><input type=hidden name=Mfn><input type=hidden name=usuario value=<?php echo $arrHttp["usuario"]?>><input type=hidden nam